// определение повторного максимума
function calc1PM(){

    var bbweight = $("#bbweight").val()*1, reps = $("#reps").val()*1;

    var bresult = bbweight * (36 / (37-reps));
    bresult = bresult.toFixed(0);
    $(".brzyckiresult").text(bresult);

};

var placeHolder = "для новичка";

function levelDefiner() {

    var bodyweight = $("#weight").val()*1;
    var pm = $(".brzyckiresult").text();

    $(".fieldsetPM table th, .fieldsetPM table td").removeClass("exactPMfromTable");

    if(!bodyweight || !pm){
        if(!bodyweight){
            $("#weight").addClass("redBorder");
        } else if (!pm){
            $(".brzyckiresult").addClass("redBorder");
        }
    } else if (bodyweight > 0 && pm > 0) {
        Cookies.set('weight', bodyweight, { expires: 90 });
        $("#weight, .brzyckiresult").removeClass("redBorder");

        // проходит по всем весам тела в первом столбце и запихивает их в массив  bWeights
        var bWeights = new Array();
        $(".fieldsetPM table tr:not(.firstRow)").each(function(){
            var thisBW = $(this).children(":first").text();
            bWeights.push(thisBW);
        });

        // в массиве bWeights находит значение массы тела, наиболее близкое к массе тела пользователя - closestWeigth
        var closestWeigth = bWeights.reduce(function (prev, curr) {
          return (Math.abs(curr - bodyweight) < Math.abs(prev - bodyweight) ? curr : prev)*1;
        });
        // alert("Блиайший вес: "+closestWeigth);

        $(".fieldsetPM td").removeClass("siblings");

        // проходит по всем ПМ из строки, которая соответсвует массе пользователя и запихивает их в массив pmsFromThisRow
        var pmsFromThisRow = new Array();

        $(".fieldsetPM table tr td:first-child").filter(function(){
            return $(this).text() == closestWeigth;
        }).addClass("exactPMfromTable").siblings().addClass("siblings");

        $(".siblings").each(function(){
            var text = $(this).text();
            pmsFromThisRow.push(text);
        });


        // в массиве pmsFromThisRow находит значение ПМ, наиболее близкое к ПМ пользователя - closestPM
        var closestPM = pmsFromThisRow.reduce(function (prev, curr) {
          return (Math.abs(curr - pm) < Math.abs(prev - pm) ? curr : prev);
        });
        // alert("Ближайший ПМ: "+closestPM);

        $(".fieldsetPM").removeClass("fadeTable, exactPMfromTable");
        // var closestPMForUser = $(".siblings:contains("+closestPM+")");

        var closestPMForUser = $(".siblings").filter(function(){
            return $(this).text() == closestPM;
        });

        $(".siblings").filter(function(){
            return $(this).text() == closestPM;
        }).addClass("exactPMfromTable");

        // closestPMForUser.addClass("exactPMfromTable");

        $(".fieldsetPM tr:not(.exactPMfromTable)").addClass("fadeTable");
        var number = $(".siblings").index(closestPMForUser)+2;

        $('.fieldsetPM th:nth-child('+number+')').addClass("exactPMfromTable");

        var levelFinally = $("#txtHint table th:nth-child("+number+")").text();

        if(number == 4){
            $('input[id=yesBarbExp]').val('средний уровень');
            placeHolder = "для среднего уровня";
        } else if(number > 4) {
            $('input[id=yesBarbExp]').val('продвинутый уровень');
            placeHolder = "для продвинутого уровня";
        } else if(number < 4) {
            $('input[id=yesBarbExp]').val('новичок');
            placeHolder = "для новичка";
        }

        $(".thisIsYourLevelCockSucker").text(levelFinally);
        $(".thisIsYourLevelCockSucker").hide();
        $(".thisIsYourLevelCockSucker").show(10);

        // $("#userLevelPlaceHolder").text(placeHolder);
        $("#pickBtn").empty();
        $("#pickBtn").append("Подобрать программу " + placeHolder);
        $("#pickBtn").show();

    }
};


$("#reps").change(calc1PM);
$("#reps").change(levelDefiner);

