    function cutThisProperly(x) {  // обрезает(не округляя) отправленное число до 2х цифр после запятой

        var digitFromButton = x.toFixed(4);
        var newY = digitFromButton.toString();
        var newX = newY;
        var arr = newX.split('.');
        var a = arr[0];
        var b = arr[1][0];
        var c = arr[1][1];

        if (c && (b == 0) && (c == 0)) {
            return a * 1;
        } else if (c && (b !== 0) && (c == 0)) {
            return (a + '.' + b) * 1;
        } else if (c && (b !== 0) && (c !== 0)) {
            return (a + '.' + b + c) * 1;
        } else if (!c && (b !== 0)) {
            return (a + '.' + b) * 1;
        } else if (!c && b == 0) {
            return a * 1;
        } else if (!b) {
            return a * 1;
        }
    };


